import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { IconAlert } from 'src/app/alerts/alerts';
import { AuthService } from 'src/app/services/auth.service';
import { AuthResponse } from 'src/app/types/auth.response.type';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html'
})
export class LoginPageComponent {
  loading: boolean = false;
  loginForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private _authService: AuthService
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]]
    });
  }

  get email() {
    return this.loginForm.get('email');
  }

  getEmailErrorMessage() {
    if (this.email?.hasError('required')) {
      return 'El email es obligatorio.';
    }
    if (this.email?.hasError('email')) {
      return 'Por favor introduce un email válido.';
    }
    return '';
  }

  onSubmit(): void {
    if (!this.loginForm.valid) { return; }
    this.loading = true;
    this._authService
      .login(this.loginForm.value)
      .subscribe((res: AuthResponse) => {
        this.loading = false;
        if (!(res as AuthResponse)) {
          IconAlert('error', 'Error al iniciar sesión');
          return;
        } else {
          console.log(res)
          IconAlert('success', 'Inicio de sesión exitoso');
        }
        // this._router.navigate(['/dashboard']);
      });
  }
}
