import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Task } from 'src/app/types/task.type';

const tasks1: Task[] = [
  {
    id: "1",
    title: "Tarea 1",
    description: "Descripción de la tarea 1",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "2",
    title: "Tarea 2",
    description: "Descripción de la tarea 2",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "3",
    title: "Tarea 3",
    description: "Descripción de la tarea 3",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "4",
    title: "Tarea 4",
    description: "Descripción de la tarea 4",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "5",
    title: "Tarea 1",
    description: "Descripción de la tarea 1",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "6",
    title: "Tarea 2",
    description: "Descripción de la tarea 2",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "7",
    title: "Tarea 3",
    description: "Descripción de la tarea 3",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "8",
    title: "Tarea 4",
    description: "Descripción de la tarea 4",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "6",
    title: "Tarea 2",
    description: "Descripción de la tarea 2",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "7",
    title: "Tarea 3",
    description: "Descripción de la tarea 3",
    creation_date: new Date(),
    completed: false,
  },
  {
    id: "8",
    title: "Tarea 4",
    description: "Descripción de la tarea 4",
    creation_date: new Date(),
    completed: false,
  }

];

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html'
})
export class DashboardComponent {
  taskForm: FormGroup;
  tasks: Task[] = tasks1;
  isEditing: boolean = false;
  taskToEdit: Task | null = null;
  loading: boolean = false;

  constructor(private fb: FormBuilder) {
    this.taskForm = this.fb.group({
      title: ['', Validators.required],
      description: ['', Validators.required],
    });
  }

  ngOnInit(): void {
    // const savedTasks = localStorage.getItem('tasks');
    // if (savedTasks) {
    //   this.tasks = JSON.parse(savedTasks);
    // }
  }

  addTask(): void {
    if (this.isEditing && this.taskToEdit) {
      // Editar tarea existente
      const index = this.tasks.findIndex((t) => t.id === this.taskToEdit?.id);
      if (index !== -1) {
        this.tasks[index] = {
          ...this.taskToEdit,
          ...this.taskForm.value,
        };
      }
      this.isEditing = false;
      this.taskToEdit = null;
    } else {
      // Agregar nueva tarea
      const newTask: Task = {
        id: "",
        title: this.taskForm.value.title,
        description: this.taskForm.value.description,
        creation_date: new Date(),
        completed: false,
      };
      this.tasks.push(newTask);
    }

    this.saveTasks();
    this.taskForm.reset();
  }

  toggleTaskStatus(task: Task): void {
    task.completed = !task.completed;
    this.saveTasks();
  }

  editTask(task: Task): void {
    this.isEditing = true;
    this.taskToEdit = task;
    this.taskForm.patchValue(task);
  }

  deleteTask(id: string): void {
    this.tasks = this.tasks.filter((task) => task.id !== id);
    this.saveTasks();
  }

  saveTasks(): void {
    localStorage.setItem('tasks', JSON.stringify(this.tasks));
  }

  logout(): void {
    // Simula el cierre de sesión
    alert('Cerrando sesión...');
    // Redirigir al inicio de sesión (puedes usar el Router aquí)
  }
}
<div class="min-h-screen bg-gray-100 flex flex-col items-center">
  <!-- Navbar fijo -->
  <nav class="fixed top-0 w-full bg-white shadow-md flex justify-between items-center">
    <div></div>
    <button
      (click)="logout()"
      class="bg-red-600 text-white px-4 py-2 my-1 rounded-lg hover:bg-red-700"
    >
      Cerrar sesión
    </button>
  </nav>

  <!-- Contenedor principal centrado -->
  <div class="flex gap-6 px-10 w-full max-w-6xl pt-20">
    <!-- Sidebar: Formulario de Nueva Tarea -->
    <div class="w-1/3 bg-white p-6 rounded-lg shadow-md h-fit sticky top-20">
      <h2 class="text-xl font-bold mb-4 text-blue-700">Agregar nueva tarea</h2>
      <form [formGroup]="taskForm" (ngSubmit)="addTask()">
        <div class="mb-4">
          <label class="block text-sm font-medium text-gray-700">Título</label>
          <input
            formControlName="title"
            type="text"
            class="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-sm"
          />
        </div>
        <div class="mb-4">
          <label class="block text-sm font-medium text-gray-700">Descripción</label>
          <textarea
            formControlName="description"
            rows="3"
            class="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 text-sm"
          ></textarea>
        </div>
        <button
          type="submit"
          [disabled]="taskForm.invalid"
          class="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 w-full disabled:opacity-50"
        >
          Agregar tarea
        </button>
      </form>
    </div>

    <!-- Sección de Tareas -->
    <div class="w-2/3 h-screen pt-[-20] overflow-y-auto">
      <!-- Grid de Tareas -->
      <div *ngIf="tasks.length > 0" class="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div
          *ngFor="let task of tasks"
          class="bg-white p-6 rounded-lg shadow-lg relative border-l-4"
          [ngClass]="task.completed ? 'border-green-500' : 'border-yellow-500'"
        >
          <h3 class="text-lg font-bold text-gray-800">{{ task.title }}</h3>
          <p class="text-sm text-gray-600 mb-2">{{ task.description }}</p>
          <p class="text-xs text-gray-400">Creado el: {{ task.creation_date | date }}</p>

          <div class="flex items-center justify-between mt-4">
            <label class="flex items-center text-sm">
              <input
                type="checkbox"
                [checked]="task.completed"
                (change)="toggleTaskStatus(task)"
                class="mr-2"
              />
              {{ task.completed ? 'Completada' : 'Pendiente' }}
            </label>
            <div class="space-x-4">
              <button
                (click)="editTask(task)"
                class="text-blue-600 hover:underline text-sm"
              >
                Editar
              </button>
              <button
                (click)="deleteTask(task.id)"
                class="text-red-600 hover:underline text-sm"
              >
                Eliminar
              </button>
            </div>
          </div>
        </div>
      </div>

      <div *ngIf="tasks.length > 0" class="h-10"></div>

      <p *ngIf="tasks.length === 0" class="text-gray-600 text-center mt-6">
        No hay tareas pendientes.
      </p>
    </div>
  </div>
</div>
